// 今日の日付を取得
const today = new Date();
const options = { 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric',
  weekday: 'long'
};
const dateJa = today.toLocaleDateString('ja-JP', options);

// 出力
return [{
  json: {
    date: dateJa,
    timestamp: today.toISOString(),
    message: `おはようございます！今日は${dateJa}です。`
  }
}];

// 天気データを整形
const weatherData = $input.item.json;
const today = weatherData.forecasts[0];
const tomorrow = weatherData.forecasts[1];

// メッセージを作成
const message = `
📅 ${$('get-date').item.json.date}

🌤️ **今日の天気（${weatherData.location.city}）**
${today.telop}
最高気温: ${today.temperature.max?.celsius || '---'}℃
最低気温: ${today.temperature.min?.celsius || '---'}℃

☔ **明日の天気**
${tomorrow.telop}
最高気温: ${tomorrow.temperature.max?.celsius || '---'}℃
最低気温: ${tomorrow.temperature.min?.celsius || '---'}℃

詳細: ${weatherData.link}
`;

return [{
  json: {
    message: message,
    weatherData: weatherData
  }
}];
