// デバッグ: すべてのバイナリデータを確認
  console.log('=== BINARY DATA DEBUG ===');
  console.log('Form Trigger binary:', $('Form Trigger').item.binary);
  console.log('Form Trigger binary keys:', $('Form Trigger').item.binary ? Object.keys($('Form Trigger').item.binary) :
  'No binary');
  console.log('Input binary:', $input.item.binary);
  console.log('Input binary keys:', $input.item.binary ? Object.keys($input.item.binary) : 'No binary');

  // フォームデータの取得
  const formData = $('Form Trigger').item.json;

  // 各ノードからデータを安全に取得（存在しない場合はデフォルト値を使用）
  let imageAnalysis = "美味しそうな料理の写真です。";
  let catchphrase = "本日のおすすめメニュー";
  let description = "ぜひお試しください。";

  // 画像解析が有効な場合
  try {
    const imageAnalysisNode = $('画像解析 (Claude Vision)');
    if (imageAnalysisNode && imageAnalysisNode.item && imageAnalysisNode.item.json) {
      imageAnalysis = imageAnalysisNode.item.json.content[0].text;
    }
  } catch (e) {
    console.log('画像解析ノードが無効またはエラー:', e.message);
  }

  // キャッチフレーズ生成の結果を取得
  try {
    const catchphraseNode = $('キャッチフレーズ生成');
    if (catchphraseNode && catchphraseNode.item && catchphraseNode.item.json) {
      catchphrase = catchphraseNode.item.json.content[0].text;
    }
  } catch (e) {
    console.log('キャッチフレーズ生成ノードのエラー:', e.message);
  }

  // 説明文生成の結果を取得
  try {
    const descriptionNode = $('説明文生成');
    if (descriptionNode && descriptionNode.item && descriptionNode.item.json) {
      description = descriptionNode.item.json.content[0].text;
    }
  } catch (e) {
    console.log('説明文生成ノードのエラー:', e.message);
  }

  // カテゴリに応じた装飾の選択
  let decoration = {};
  switch(formData.カテゴリ) {
    case 'コーヒー ☕':
      decoration = {
        mainEmoji: '☕',
        subEmojis: ['✨', '⭐'],
        borderStyle: '━━━━━━━━━━━━━━━━━━━━'
      };
      break;
    case 'ドリンク🍹':
      decoration = {
        mainEmoji: '🍹',
        subEmojis: ['🌴', '🌊'],
        borderStyle: '〜〜〜〜〜〜〜〜〜〜〜〜〜〜〜'
      };
      break;
    case 'スイーツ 🧁':
      decoration = {
        mainEmoji: '🧁',
        subEmojis: ['🎉', '💕'],
        borderStyle: '🌈 ═══════════════ 🌈'
      };
      break;
    case 'ランチ🍝':
      decoration = {
        mainEmoji: '🍝',
        subEmojis: ['🌿', '🥗'],
        borderStyle: '◆◇◆◇◆◇◆◇◆◇◆◇◆◇◆'
      };
      break;
    case 'BAR🍺':
      decoration = {
        mainEmoji: '🍺',
        subEmojis: ['🎆', '🌙'],
        borderStyle: '★☆★☆★☆★☆★☆★☆★'
      };
      break;
    default:
      decoration = {
        mainEmoji: '🍴',
        subEmojis: ['✨', '⭐'],
        borderStyle: '━━━━━━━━━━━━━━━━━━━━'
      };
  }

  // 現在時刻の取得
  const now = new Date();
  const hours = now.getHours();
  let timeGreeting = '';
  if (hours < 12) {
    timeGreeting = 'おはようございます！朝の';
  } else if (hours < 17) {
    timeGreeting = 'こんにちは！午後の';
  } else {
    timeGreeting = 'お疲れ様です！夕方の';
  }

  // Slack投稿メッセージの組み立て (Slackのマークアップ形式)
  const slackMessage = `
  ┌─────────────────────┐
  │  ${decoration.mainEmoji} 本日のSpecial ${decoration.mainEmoji}  │
  └─────────────────────┘

  ${decoration.subEmojis[0]} *${catchphrase}* ${decoration.subEmojis[0]}

  ${description}

  ${decoration.borderStyle}
  📍 場所: ${formData.場所}
  ⏰ 提供時間: ${formData.提供時間}
  ${decoration.borderStyle}

  🎯 ${timeGreeting}ひととき、
  ${decoration.mainEmoji} でほっと一息つきませんか？

  #tam and co. #今日のオススメ #${formData.カテゴリ.replace(' ', '')}
  `;

  // Form Triggerから直接バイナリデータを取得
  const originalBinary = $('Form Trigger').item.binary;

  // バイナリデータのキー名を統一（重要！）
  let normalizedBinary = {};
  if (originalBinary && Object.keys(originalBinary).length > 0) {
    const firstKey = Object.keys(originalBinary)[0];
    console.log('Original binary key:', firstKey);
    normalizedBinary = { data: originalBinary[firstKey] };
  } else if ($input.item.binary && Object.keys($input.item.binary).length > 0) {
    const firstKey = Object.keys($input.item.binary)[0];
    console.log('Input binary key:', firstKey);
    normalizedBinary = { data: $input.item.binary[firstKey] };
  }

  console.log('Normalized binary:', normalizedBinary);

  // 結果を返す（バイナリデータも含める）
  return {
    json: {
      slackMessage: slackMessage,
      menuName: formData.メニュー名,
      category: formData.カテゴリ,
      decoration: decoration
    },
    // バイナリデータを'data'というキー名で確実に渡す
    binary: normalizedBinary
  };
